import { useEffect, useState, useContext } from "react";
import {SocketContext} from './socket';

function ContactItem(props) {

  const socket = useContext(SocketContext);

  const item = props.item;

  const [chat, setChat] = useState(null);
  const [unreadMessages, setUnreadMessages] = useState(0);

  async function fetchChat(item) {
    try {
      await fetch(
        `http://localhost:3000/chat/find?users=${props.user._id}&users=${item._id}`
      )
        .then((res) => {
          return res.json();
        })
        .then((data) => {
         setChat(data);
         if(data.messages.length){
         setUnreadMessages(
            data.messages.filter((item) => !item.read_by.includes(props.user._id))
              .length
          )}
        });
    } catch (error) {
    }
  }


  useEffect(() => {
    if(item)fetchChat(item);

    socket.on("receive_message", (data) => {
        unreadMessages(current=>current+1)
       });

       return () => {
         socket.off("receive_message");
       };
  }, []);

  return (
    <div
      className="ContactItem"
      onClick={() => {
        if (!props.isInChatList(item._id)) {
          props.setChatList((current) => [
            ...current,
            {
              id: item._id,
              name: item.first_name + " " + item.last_name,
              img_url: item.img_url,
              chat_id: chat._id,
            },
          ]);
        }
      }}
    >
      <div className="user">
        <img src={item.img_url}></img>
        <div>{item.first_name + " " + item.last_name} </div>
        { unreadMessages>0 && <div className="alert">{unreadMessages} </div>}
      </div>
    </div>
  );
}

export default ContactItem;
