function ContactItem(props) {

    const item=props.item

    async function fetchChat() {
        try {
          await fetch(`http://localhost:3000/chat/${props.data.chat_id}`)
            .then((res) => {
              return res.json();
            })
            .then((data) => {
              // console.log(data)
              setChat(data);
              setMessages(data.messages);
            });
        } catch (error) {
          console.log(error);
        }
      }

    async function fetchChat(item) {
        try {
          await fetch(
            `http://localhost:3000/chat/find?users=${props.user._id}&users=${item._id}`
          )
            .then((res) => {
              return res.json();
            })
            .then((data) => {
              props.setChatList((current) => [
                ...current,
                {
                  id: item._id,
                  name: item.first_name + " " + item.last_name,
                  img_url: item.img_url,
                  chat_id:  data._id
                },
              ]);
            });
        } catch (error) {
          console.log(error);
        }
      }

  return (
    <div
      className="ContactItem"
      onClick={() => {
        if (!props.isInChatList(item._id)) {
          fetchChat(item);
        }
      }}
    >
      <div className="user">
        <img src={item.img_url}></img>
        <div>{item.first_name + " " + item.last_name} </div>
      </div>
    </div>
  );
}

export default ContactItem;
