import "./App.css";
import { useEffect, useState } from "react";
import Timeline from "./components/Timeline";
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  Link,
  useNavigate,
  redirect,
} from "react-router-dom";
import CreatePost from "./components/CreatePost";
import Login from "./components/Login";
import EditPost from "./components/EditPost";
import SignUp from "./components/SignUp";
import Profile from "./components/Profile";
import SearchResults from "./components/SearchResults";



function App() {
  const [user, setUser] = useState(null);
  const [query, setQuery] = useState("");
  const [searchResult,setSearchResult] = useState(null)
  const navigate = useNavigate()

  async function signIn(username, password) {
    try {
      await fetch("http://localhost:3000/sign-in", {
        method: "POST",
        body: JSON.stringify({
          username: username,
          password: password,
        }),
        headers: { "content-type": "application/json" },
      })
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setUser(data);
          localStorage.setItem("user", JSON.stringify(data));
        });
    } catch (error) {}
  }

  async function search(query) {
    try {
      await fetch(`http://localhost:3000/search?query=${query}`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setSearchResult(data)
        });
    } catch (error) {}
  }

  const handleSubmit = (event) => {
    event.target.reset();
    event.preventDefault(); // 👈️ prevent page refresh
    search(query)
    navigate('/')
    setQuery("");
    // 👇️ clear all input values in the form
  };

  useEffect(() => {
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      setUser(foundUser);
    }
  }, []);

  return (
    <BrowserRouter>
      <div className="App">
        <div className="header">
          {user && (
            <div>
              <form onSubmit={handleSubmit}><button>Search</button><input placeholder="Search"  onChange={(event) => setQuery(event.target.value)}></input></form>
              <Link to={process.env.PUBLIC_URL + `/user/${user._id}/profile`}>
                {user.first_name + " " + user.last_name}
              </Link>
            </div>
          )}
        </div>
        <Routes>
          {!user && (
            <Route
              path={process.env.PUBLIC_URL + "/"}
              element={<Login user={user} signIn={signIn} />}
            />
          )}
          {user && (
            <Route
              path={process.env.PUBLIC_URL + "/"}
              element={<Timeline user={user} />}
            />
          )}
          <Route
            path={process.env.PUBLIC_URL + "/sign-up"}
            element={<SignUp user={user} signIn={signIn} />}
          />
          <Route
            path={process.env.PUBLIC_URL + "/user/:id/profile"}
            element={<Profile />}
          />
          <Route
            path={process.env.PUBLIC_URL + "/search"}
            element={<SearchResults users={searchResult} />}
          />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
