import "../styles/Comment.css"
import { format } from "date-fns";

function Comment(props) {
  const [author, setAuthor] = useState(null);

  async function fetchAuthor() {
    await fetch(
      `http://localhost:3000/user/${props.comment.author_id}/details`
    )
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setAuthor(data);
      });
  }

  return (
    <div className="Comment">
         <div>
            {author && <div className="author"><img src={author.img_url}/>{author.first_name + " " + author.last_name}</div>}
                     <div className="date">
                         <div>{format(new Date(props.comment.date), 'H:mm' )}</div>
                         <div>{format(new Date(props.comment.date), 'MM/dd/yy' )}</div>
                     </div>
         </div>
        <div>{props.comment.content}</div>
    </div>
  );
}

export default Comment;
