import Chat from "./Chat";
import "../styles/Chats.css";

function Chats(props) {
  function removeChat(id) {
    props.setChatList(
      props.chatList.filter(function (chat) {
        return chat.id !== id;
      })
    );
  }

  async function fetchChat() {
    try {
      await fetch(
        `http://localhost:3000/chat?users=${props.user._id}&users=${props.data.id}`
      )
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setChat(data);
          setMessages(data.messages);
          props.joinRoom(data._id)
        });
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div className="Chats">
      {props.chatList &&
        props.chatList
          .slice(-3)
          .reverse()
          .map((item, i) => {
            return (
              <Chat
                key={i}
                data={item}
                removeChat={removeChat}
                user={props.user}
                sendMessage={props.sendMessage}
                joinRoom={props.joinRoom}
              />
            );
          })}
    </div>
  );
}

export default Chats;
