import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-regular-svg-icons";
import { useEffect, useState } from "react";
import { format } from "date-fns";
import Comment from "../components/Comment";
import "../styles/Post.css";
import { useNavigate } from "react-router-dom";
import CreateComment from "./CreateComment";

function Post(props) {
  const navigate = useNavigate();
  const [comments, setComments] = useState(null);
  const [author, setAuthor] = useState(null);
  const [commentsVisible, setCommentsVisible] = useState(false);
  const [update, setUpdate] = useState(0);

  async function fetchComments() {
    await fetch(`http://localhost:3000/post/${props.post._id}/comments`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setComments(data);
      });
  }

  async function fetchAuthor() {
    await fetch(
      `http://localhost:3000/post/${props.post._id}/author/${props.post.author_id}`
    )
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setAuthor(data);
      });
  }

  useEffect(() => {
    fetchComments();
    fetchAuthor();
    setUpdate(0);
  }, [update]);

  return (
    <div className="Post">
      <div>
        {author && (
          <div className="author">
            <img src={author.img_url}></img>
            <div> {author.first_name + " " + author.last_name} </div>
          </div>
        )}
        <div className="date">
          <div>{format(new Date(props.post.date), "MMMM do, yyyy ")}</div>
          <div>{format(new Date(props.post.date), "H:mm")}</div>
          {props.post.author_id === props.user._id && (
            <button
              onClick={() => {
                props.openModal();
                props.setPost_id(props.post._id);
                props.setPostContent(props.post.content);
              }}
            >
              Edit
            </button>
          )}
        </div>
      </div>
      <div className="content">{props.post.content}</div>
      <div className="details">
        details
        <div className="likes">
        <FontAwesomeIcon
              icon={faHeart}
            />
        </div>
        {props.post.likes.length}
      </div>
      {comments && <div className="comments-count">
        Comments
        {" "+comments.length}
      </div>}
      {!commentsVisible && (
        <button
          onClick={() => {
            fetchComments();
            setCommentsVisible(true);
          }}
        >
          Show Comments
        </button>
      )}
      {commentsVisible && (
        <div className="comments">
          <button
            onClick={() => {
              setCommentsVisible(false);
            }}
          >
            hide comments
          </button>
          <CreateComment
            setUpdate={setUpdate}
            user={props.user}
            post_id={props.post._id}
          />
          {comments.map((comment, i) => {
            return <Comment comment={comment} key={i} setUpdate={setUpdate} />;
          })}
        </div>
      )}
    </div>
  );
}

export default Post;
