function Chats(props) {

    function removeChat(id){
        chatList.filter(function(chat){
            return chat.id !== id
        })
    }

    return (
      <div className="Chats">
        { props.chatList && props.chatList.map((item,i)=>{
            return <Chat key = {i} data = {item} />
        })
        }
      </div>
    );
}

