import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBell } from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import React from "react";
import Modal from "react-modal";
import "../styles/HeaderContent.css";

const customStyles = {
  content: {
    top: "0%",
    left: "auto",
    right: "0",
    bottom: "auto",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "none",
  },
};

function HeaderContent(props) {
  const [query, setQuery] = useState("");
  const [modalIsOpen, setIsOpen] = useState(false);
  const [friendRequests, setFriendRequests] = useState([]);

  const navigate = useNavigate();

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
  }

  function closeModal() {
    setIsOpen(false);
  }

  async function search(query) {
    try {
      await fetch(`http://localhost:3000/search?query=${query}`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          props.setSearchResult(data);
        });
    } catch (error) {}
  }

  async function getUserFriendRequests() {
    try {
      await fetch(
        `http://localhost:3000/user/${props.user._id}/friend-requests`
      )
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setFriendRequests(data);
        });
    } catch (error) {}
  }

  async function resolveFriendRequest(id, accept) {
    await fetch(
      `http://localhost:3000/user/${props.user._id}/friend-request/resolve?user_id=${id}&accept=${accept}`,
      {
        method: "PUT",
      }
    );
    props.setUpdateUser(true);
    getUserFriendRequests();
  }

  const handleSubmit = (event) => {
    event.target.reset();
    event.preventDefault(); // ðŸ‘ˆï¸ prevent page refresh
    search(query);
    navigate(process.env.PUBLIC_URL + `/search?query=${query}`);
    setQuery("");
    // ðŸ‘‡ï¸ clear all input values in the form
  };

  useEffect(() => {
    getUserFriendRequests();
  }, [props.user]);

  return (
    <div className="HeaderContent">
      {props.isAuthenticated && (
        <div>
          <form onSubmit={handleSubmit}>
            <button>Search</button>
            <input
              placeholder="Search"
              required
              onChange={(event) => setQuery(event.target.value)}
            ></input>
          </form>
          <div>
            <FontAwesomeIcon className={friendRequests.length ? "alert" : ""} icon={faBell} onClick={openModal} />
            <div className="profile" onClick={()=>{navigate(process.env.PUBLIC_URL + `/user/${props.user._id}/profile`)}}><img src={props.user.img_url}></img><div>{props.user.first_name + " " + props.user.last_name}</div></div>
          </div>
          <Modal
            isOpen={modalIsOpen}
            onAfterOpen={afterOpenModal}
            onRequestClose={closeModal}
            style={customStyles}
            contentLabel="Example Modal"
            ariaHideApp={false}
          >
            <div>Notification</div>
            {friendRequests && (
              <div>
                {friendRequests.map((item, i) => {
                  return (
                    <div key={i}>
                      <div
                        onClick={() => {
                          navigate(
                            process.env.PUBLIC_URL + `/user/${item._id}/profile`
                          );
                        }}
                      >
                        {item.first_name + " " + item.last_name}
                      </div>
                      <div>
                        <button
                          onClick={() => {
                            resolveFriendRequest(item._id, true);
                          }}
                        >
                          Confirm
                        </button>
                        <button
                          onClick={() => {
                            resolveFriendRequest(item._id, false);
                          }}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </Modal>
        </div>
      )}
    </div>
  );
}

export default HeaderContent;
