import { useEffect, useState } from "react";
import Chat from "./Chat";
import "../styles/Chats.css"

function Chats(props) {
    const [chat,setChat] = useState(null)

    function removeChat(id){
        props.setChatList(props.chatList.filter(function(chat){
            return chat.id !== id
        }))
    }

    return (
      <div className="Chats">
        { props.chatList && props.chatList.map((item,i)=>{
            return <Chat key = {i} data = {item} removeChat={removeChat}/>
        })
        }
      </div>
    );
}

export default Chats;
