//import "../styles/Chat.css"
import { useEffect, useState } from "react";

function Chat(props) {
  const [chat, setChat] = useState(null);
  const [text, setText] = useState("");
  const [messages,setMessages] = useState([])

  async function fetchChat() {
    await fetch(
      `http://localhost:3000/chat?users=${props.user._id}&users=${props.data.id}`
    )
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setChat(data);
        setMessages(data.messages);
      });
  }

  async function postMessage() {
    await fetch(`http://localhost:3000/chat/${chat._id}/message?author=${props.user._id}`, {
      method: "POST",
      body: JSON.stringify({
        text: text,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });
  }

  const handleSubmit = (event) => {
    event.target.reset();
    event.preventDefault(); // 👈️ prevent page refresh
    postMessage();
    setText("");
    // 👇️ clear all input values in the form
  };

  useEffect(() => {
    fetchChat();
  }, []);

  return (
    <div className="Chat">
      <div className="top">
        <div> {props.data.name} </div>
        <button
          onClick={() => {
            props.removeChat(props.data.id);
          }}
        >
          close
        </button>
      </div>
      <div className="content">
          {messages.map((message,i)=>{return <div key={i}>{message.text}</div>})}
      </div>
      <div className="bottom">
        <form className="comment-form" onSubmit={handleSubmit}>
          <input
            placeholder="Write something..."
            name="text"
            required
            onChange={(event) => setText(event.target.value)}
          ></input>
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default Chat;
