import Post from "./Post";
import Modal from "react-modal";
import CreatePost from "./CreatePost";
import EditPost from "./EditPost";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function MyProfile(props) {
  const [user, setUser] = useState(null);
  const [posts,setPosts] = useState([])
  const [postContent, setPostContent] = useState("");
  const [post_id, setPost_id] = useState(null);
  const [modalType, setModalType] = useState("edit");
  const [modalIsOpen, setIsOpen] = React.useState(false);

  const params = useParams();

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
  }

  function closeModal() {
    setModalType("edit");
    setIsOpen(false);
  }

  async function fetchPosts(user_id) {
    await fetch(`http://localhost:3000/user/${user_id}/posts`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setPosts(current => [...current, ...data]);
      });
  }

  useEffect(() => {
    async function fetchUser() {
      await fetch(`http://localhost:3000/user/${params.id}`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setUser(data);
        });
    }
    fetchUser();
    fetchPosts(params.id)
  }, []);



  return (
    <div className="MyProfile">
      {user && (
        <div>
          <h1>{user.first_name + " " + user.last_name}</h1>
          <h2>{user.email}</h2>
        </div>
      )}
                 {posts !== null &&<div className="posts">{
        posts.map((post, i) => {
          return (
            <Post
              user={props.user}
              post={post}
              key={i}
              openModal={openModal}
              setPostContent={setPostContent}
              setPost_id={setPost_id}
            />
          );
        })}</div>}
              <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
        ariaHideApp={false}
      >
        {modalType === "edit" && (
          <EditPost
            postContent={postContent}
            post_id={post_id}
            posts={posts}
            setPosts={setPosts}
            closeModal={closeModal}
          />
        )}
        {modalType === "create" && (
          <CreatePost
            user={props.user}
            setPosts={setPosts}
            closeModal={closeModal}
          />
        )}
      </Modal>
    </div>
  );
}

export default MyProfile;
