import { set } from "date-fns";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import MyProfile from "./MyProfile";

function Profile(props) {
  const [user, setUser] = useState(null);
  const [friend ,setFriend] = useState(false)
  const [requestPending ,setRequestPending] = useState(false)
  const [requestIncoming ,setRequestIncoming] = useState(false)

  const params = useParams();

  useEffect(() => {
    async function fetchUser() {
      await fetch(`http://localhost:3000/user/${params.id}`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setUser(data);
        });
    }
    fetchUser();
    if(isFriend(params.id)) setFriend(true)
    if(isPending(params.id)) setRequestPending(true)
  }, []);

  function isFriend(id){
    props.user.friend_list.map((item)=>{
        if(item === id) return true
    })
    return false
  }

  function isPending(id){
    props.user.friend_requests.map((item)=>{
      if(item === id) return true
  })
  return false
  }

  async function sendFriendRequest(){
    await fetch(
      `http://localhost:3000/user/${params.id}/friend-request?user_id=${props.user._id}`,
      {
        method: "PUT",
      }
    );
    setRequestPending(true)
  }

  if(props.user._id === params.id) return <MyProfile user={props.user} />

  return (
    <div className="Profile">
      {user && (
        <div>
          <h1>{user.first_name + " " + user.last_name}</h1>
          <h2>{user.email}</h2>
          { !friend && !requestPending  && <button onClick={sendFriendRequest}>Add Friend</button>}
          { requestPending  && <h2>Friend Request Sent</h2>}
        </div>
      )}
    </div>
  );
}

export default Profile;
