//import "../styles/Chat.css"
import { useEffect, useState } from "react";

function Chat(props) {
    const [chat,setChat] = useState(null)

    async function fetchChat() {
        await fetch(`http://localhost:3000/chat?user=${props.user._id},${props.data.id}`)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
           setChat(data)
          });
      }

    return (
      <div className="Chat">
        <div className="top">
            <div> {props.data.name} </div>
            <button onClick={()=>{props.removeChat(props.data.id)}}>close</button>
        </div>
      </div>
    );
}

export default Chat;
