import { useEffect, useState } from "react";

function ContactItem(props) {
  const item = props.item;

  const [chat, setChat] = useState(null);
  const [unreadMessages, setUnreadMessages] = useState(0);

  async function fetchChat(item) {
    try {
      await fetch(
        `http://localhost:3000/chat/find?users=${props.user._id}&users=${item._id}`
      )
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          if(!data) createChat()
          setChat(data);
        });
    } catch (error) {
      console.log(error);
    }
  }

  async function createChat() {
    try {
      await fetch(
        `http://localhost:3000/chat/createusers=${props.user._id}&users=${item._id}`,
        { method: "POST" }
      )
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setChat(data);
        });
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchChat(item);
  }, []);

  useEffect(() => {
    if (chat !== null) {
      setUnreadMessages(
        chat.messages.filter((item) => !item.read_by.includes(props.user._id))
          .length
      );
    }
  }, [chat]);

  return (
    <div
      className="ContactItem"
      onClick={() => {
        if (!props.isInChatList(item._id)) {
          props.setChatList((current) => [
            ...current,
            {
              id: item._id,
              name: item.first_name + " " + item.last_name,
              img_url: item.img_url,
              chat_id: chat._id,
            },
          ]);
        }
      }}
    >
      <div className="user">
        <img src={item.img_url}></img>
        <div>{item.first_name + " " + item.last_name} </div>
        <div>{unreadMessages} </div>
      </div>
    </div>
  );
}

export default ContactItem;
