//import "../styles/Chat.css"
import { format } from "date-fns";
import React, { useEffect } from 'react';
import Modal from 'react-modal';

const customStyles = {
    content: {
      top: "auto",
      left: "auto",
      right: "0",
      bottom: "0%",
    },
    overlay: {
      position: "fixed",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "none",
    },
  };

function Chat(props) {
    const [modalIsOpen, setIsOpen] = React.useState(false);

    function openModal() {
      setIsOpen(true);
    }

    function afterOpenModal() {
      // references are now sync'd and can be accessed.
    }

    function closeModal() {
      setIsOpen(false);
    }

    useEffect(()=>{
        if(props.chat) openModal()
    }
    ,[props.chat])

    return (
      <div>
        <button onClick={openModal}>Open Modal</button>
        <Modal
          isOpen={modalIsOpen}
          onAfterOpen={afterOpenModal}
          style={customStyles}
          contentLabel="Example Modal"
          ariaHideApp={false}
        >
      <div className="Chat">
        Chat 
      </div>
        </Modal>
      </div>
    );
}

export default Chat;
