import "../styles/Contacts.css"
function Contacts(props) {
  return (
    <div className="Contacts">
      <div>Contacts</div>
      {props.friends &&
        props.friends.map((item, i) => {
          return (
            <div
              key={i}
              onClick={() => {

                if(props.isInChatList){

                props.setChatList(current => [...current, {
                  id: item._id,
                  name: item.first_name + " " + item.last_name,
                  img_url : item.img_url
                }]);
              }
              }}
            >
              <div className="user"><img src={item.img_url}></img><div>{item.first_name + " " + item.last_name} </div></div>
            </div>
          );
        })}
    </div>
  );
}

export default Contacts;
