function HeaderContent(props) {

    const navigate = useNavigate()

    async function search(query) {
        try {
          await fetch(`http://localhost:3000/search?query=${query}`)
            .then((res) => {
              return res.json();
            })
            .then((data) => {
              setSearchResult(data)
            });
        } catch (error) {}
      }

      const handleSubmit = (event) => {

        event.target.reset();
        event.preventDefault(); // üëàÔ∏è prevent page refresh
        search(query)
        navigate(process.env.PUBLIC_URL + `/search?query=${query}`)
        setQuery("");
        // üëáÔ∏è clear all input values in the form
      };
    return (
      <div className="HeaderContent">
{props.isAuthenticated && (
    <div>
      <form onSubmit={handleSubmit}><button>Search</button><input placeholder="Search"  onChange={(event) => setQuery(event.target.value)}></input></form>
      <Link to={process.env.PUBLIC_URL + `/user/${user._id}/profile`}>
        {user.first_name + " " + user.last_name}
      </Link>
    </div>
  )}
      </div>
    );
  }

  export default HeaderContent;
