import { useState } from "react";
import { Navigate, useNavigate } from "react-router-dom";
import "../styles/Login.css"

function Login(props) {
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')
    const [loginError,setLoginError] = useState(false)
    const navigate = useNavigate()

    async function fb (){
      try {
        await fetch("http://localhost:3000/login/facebook",{
          headers: { "Access-Control-Allow-Origin":"http://localhost:3000/login/facebook" },
        })
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data)
          });
      } catch (error) {console.log(error)}
    }
    const handleSubmit = (event) => {
        event.target.reset();
        event.preventDefault();
        props.signIn(email,password)
      };

    if(props.user) return <Navigate to={process.env.PUBLIC_URL + "/"}/>

     return (
      <div className="Login">
         <form className="login-form" onSubmit={handleSubmit}>
        <input
        type={"email"}
          placeholder="Email"
          name="email"
          required
          onChange={(event) => setEmail(event.target.value)}
        ></input>
        <input
        type={'password'}
          placeholder="Password"
          name="password"
          required
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        {loginError && <div>email or Password is incorrect ! </div>}
        <button type="submit">Login</button>
        <button onClick={()=>{fb()}}>Login With Facebook</button>
        <button onClick={()=>navigate(process.env.PUBLIC_URL + "/sign-up")}>Create New Account</button>
      </form>

      </div>
    );
  }

  export default Login;
