import { useEffect, useState } from "react";
import { format } from "date-fns";
import Comment from "../components/Comment";
import "../styles/Post.css";
import { useNavigate } from "react-router-dom";
import CreateComment from "./CreateComment";

function Post(props) {
  const navigate = useNavigate();
  const [comments, setComments] = useState(null);
  const [commentsVisible, setCommentsVisible] = useState(false);
  const [update, setUpdate] = useState(0);

  async function fetchComments() {
    await fetch(`http://localhost:3000/post/${props.post._id}/comments`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setComments(data);
      });
  }

  async function fetchAuthor() {
    await fetch(`http://localhost:3000/post/${props.post._id}/author`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setComments(data);
      });
  }

  useEffect(() => {
    fetchComments();
    setUpdate(0);
  }, [update]);

  return (
    <div className="Post">
      <div>
        <div className="author">
          <img src={props.post.img_url}></img>
        <div> {props.post.author_name} </div>
        </div>
        <div className="date">
          <div>{format(new Date(props.post.date), "MMMM do, yyyy ")}</div>
          <div>{format(new Date(props.post.date), "H:mm")}</div>
         {props.post.author_id === props.user._id && <button
            onClick={() => {
              props.openModal();
              props.setPost_id(props.post._id);
              props.setPostContent(props.post.content);
            }}
          >
            Edit
          </button>}
        </div>
      </div>
      <div className="content">{props.post.content}</div>
      {!commentsVisible && (
        <button
          onClick={() => {
            fetchComments();
            setCommentsVisible(true);
          }}
        >
          Show Comments
        </button>
      )}
      {commentsVisible && (
        <div className="comments">
          <button
            onClick={() => {
              setCommentsVisible(false);
            }}
          >
            hide comments
          </button>
          <CreateComment setUpdate={setUpdate} user={props.user} post_id={props.post._id}/>
          {comments.map((comment, i) => {
            return <Comment comment={comment} key={i} setUpdate={setUpdate} />;
          })}
        </div>
      )}
    </div>
  );
}

export default Post;
