import Chat from "./Chat";
import "../styles/Chats.css"

function Chats(props) {


    function removeChat(id){
        props.setChatList(props.chatList.filter(function(chat){
            return chat.id !== id
        }))
    }

    return (
      <div className="Chats">

      </div>
    );
}

export default Chats;

{ props.chatList && props.chatList.map((item,i)=>{
  return <Chat key = {i} data = {item} removeChat={removeChat} user={props.user}/>
})
}
