import "./styles/App.css"
import React from "react";
import { useEffect, useState } from "react";
import Timeline from "./components/Timeline";
import { Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import SignUp from "./components/SignUp";
import Profile from "./components/Profile";
import SearchResults from "./components/SearchResults";
import Header from "./components/Header";
import Chats from "./components/Chats";
import { is } from "date-fns/locale";

function App() {
  const [user, setUser] = useState(null);
  const [friends, setFriends] = useState([])
  const [updateUser, setUpdateUser] = useState(false);
  const [searchResult, setSearchResult] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [chatList, setChatList] = useState([])

  useEffect(() => {
    if (user) setIsAuthenticated(true);
  }, [user]);

  const PrivateRoute = ({children }) => {
    return isAuthenticated ? children : <Navigate to="/login" />;
  };

  async function signIn(username, password) {
    try {
      await fetch("http://localhost:3000/sign-in", {
        method: "POST",
        body: JSON.stringify({
          username: username,
          password: password,
        }),
        headers: { "content-type": "application/json" },
      })
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setUser(data);
          localStorage.setItem("user", JSON.stringify(data));
        });
    } catch (error) {}
  }

  async function updateUserData() {
    try {
      await fetch(`http://localhost:3000/user/${user._id}`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setUser(data);
          localStorage.setItem("user", JSON.stringify(data));
        });
    } catch (error) {}
  }

  async function getUserFriends() {
    try {
      await fetch(`http://localhost:3000/user/${user._id}/friends`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setFriends(data);
        });
    } catch (error) {}
  }

  useEffect(() => {
    console.log('app')
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      //Update data
      updateUserData();
      const foundUser = JSON.parse(loggedInUser);
      setUser(foundUser);
    }
  }, []);

  useEffect(() => {
   getUserFriends()
  }, [user]);

  useEffect(() => {
    updateUserData();
    setUpdateUser(false);
  }, [updateUser]);

  return (
    <div className="App">
        <div className="header">
          <Header
            isAuthenticated={isAuthenticated}
            setSearchResult={setSearchResult}
            user={user}
            friends={friends}
            setUpdateUser={setUpdateUser}
          />
        </div>
      <Chats  setChatList={setChatList} chatList={chatList} user={user}/>
        <Routes>
          <Route
            path={process.env.PUBLIC_URL + "/"}
            element={
                isAuthenticated?<Timeline user={user} friends={friends} setChatList={setChatList} />: <Navigate to="/login" />
            }
          />
          <Route
            path={process.env.PUBLIC_URL + "/login"}
            element={<Login user={user} signIn={signIn} />}
          />
          <Route
            path={process.env.PUBLIC_URL + "/sign-up"}
            element={<SignUp user={user} signIn={signIn} />}
          />
          <Route
            user={user}
            path={process.env.PUBLIC_URL + "/user/:id/profile"}
            element={
              <PrivateRoute isAuthenticated={isAuthenticated}>
                <Profile user={user} setUpdateUser={setUpdateUser} />
              </PrivateRoute>
            }
          />
          <Route
            path={process.env.PUBLIC_URL + "/search"}
            element={
              <PrivateRoute isAuthenticated={isAuthenticated}>
                <SearchResults users={searchResult} />
              </PrivateRoute>
            }
          />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
    </div>
  );
}

export default App;
