import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBell,
  faHouse,
  faMagnifyingGlass,
  faArrowRightFromBracket,
} from "@fortawesome/free-solid-svg-icons";

import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import React from "react";
import Modal from "react-modal";
import "../styles/HeaderContent.css";

const customStyles = {
  content: {
    top: "0%",
    left: "auto",
    right: "3rem",
    bottom: "auto",
    boxShadow:
      "rgba(14, 30, 37, 0.12) 0px 2px 4px 0px, rgba(14, 30, 37, 0.32) 0px 2px 16px 0px",
    borderRadius: "0 0 3px 3px",
  },
  overlay: {
    position: "fixed",
    top: "5rem",
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "none",
  },
};

function HeaderContent(props) {
  const [query, setQuery] = useState("");
  const [modalIsOpen, setIsOpen] = useState(false);
  const [friendRequests, setFriendRequests] = useState([]);

  const navigate = useNavigate();

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
  }

  function closeModal() {
    setIsOpen(false);
  }

  async function search(query) {
    try {
      await fetch(`http://localhost:3000/search?query=${query}`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          props.setSearchResult(data);
        });
    } catch (error) {}
  }

  async function getUserFriendRequests() {
    try {
      await fetch(
        `http://localhost:3000/user/${props.user._id}/friend-requests`
      )
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setFriendRequests(data);
        });
    } catch (error) {}
  }

  async function resolveFriendRequest(id, accept) {
    await fetch(
      `http://localhost:3000/user/${props.user._id}/friend-request/resolve?user_id=${id}&accept=${accept}`,
      {
        method: "PUT",
      }
    );
    props.setUpdateUser(true);
    getUserFriendRequests();
  }

  const handleSubmit = (event) => {
    event.target.reset();
    event.preventDefault(); // ðŸ‘ˆï¸ prevent page refresh
    search(query);
    navigate(process.env.PUBLIC_URL + `/search?query=${query}`);
    setQuery("");
    // ðŸ‘‡ï¸ clear all input values in the form
  };

  useEffect(() => {
    getUserFriendRequests();
  }, [props.user]);

  return (
    <div className="HeaderContent">
       {!props.isAuthenticated && <div className="title"><h1><svg></svg>OdinBook</h1></div>}
      {props.isAuthenticated && (
        <div>
          <div className="header-left">
            <form onSubmit={handleSubmit} autoComplete="off">
              <button>
                <FontAwesomeIcon icon={faMagnifyingGlass} />
              </button>
              <input
                placeholder="Search"
                required
                onChange={(event) => setQuery(event.target.value)}
              ></input>
            </form>
            <FontAwesomeIcon
              icon={faHouse}
              onClick={() => {
                navigate(process.env.PUBLIC_URL + "/");
              }}
            />
          </div>
          <div className="header-right">
            <div className='notification'>
              {friendRequests.length && (
                <div className="notification-number">
                  <div>{friendRequests.length}</div>
                </div>
              )}
              <FontAwesomeIcon  className={friendRequests.length ?"alert":""} icon={faBell} onClick={openModal} />
            </div>
            <div
              className="profile"
              onClick={() => {
                navigate(
                  process.env.PUBLIC_URL + `/user/${props.user._id}/profile`
                );
              }}
            >
              <img src={props.user.img_url}></img>
              <div className={"sign-out-container"}>
                <div>{props.user.first_name + " " + props.user.last_name}</div>
                <div
                  className="sign-out"
                  onClick={() => {
                    localStorage.clear();
                    window.location.reload(false);
                  }}
                >
                  Sign out <FontAwesomeIcon icon={faArrowRightFromBracket} />
                </div>
              </div>
            </div>
          </div>
          <Modal
            isOpen={modalIsOpen}
            onAfterOpen={afterOpenModal}
            onRequestClose={closeModal}
            style={customStyles}
            contentLabel="Example Modal"
            ariaHideApp={false}
          >
            {friendRequests && (
              <div className="friend-requests">
                {friendRequests.map((item, i) => {
                  return (
                    <div key={i} className="request">
                      <img
                        src={item.img_url}
                        onClick={() => {
                          navigate(
                            process.env.PUBLIC_URL + `/user/${item._id}/profile`
                          );
                        }}
                      ></img>
                      <div>
                        <div>
                          <b
                            onClick={() => {
                              navigate(
                                process.env.PUBLIC_URL +
                                  `/user/${item._id}/profile`
                              );
                            }}
                          >
                            {item.first_name + " " + item.last_name + " "}
                          </b>
                          sent you a friend request.
                        </div>
                        <div className="button-container">
                          <button
                            onClick={() => {
                              resolveFriendRequest(item._id, true);
                            }}
                          >
                            Confirm
                          </button>
                          <button
                            onClick={() => {
                              resolveFriendRequest(item._id, false);
                            }}
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    </div>
                  );
                })}
                 {!friendRequests.length && <div>No notifications</div>}
              </div>
            )}

          </Modal>
        </div>
      )}
    </div>
  );
}

export default HeaderContent;
