//import "../styles/Chat.css"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faXmark, faReply} from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";

function Chat(props) {
  const [chat, setChat] = useState(null);
  const [text, setText] = useState("");
  const [messages,setMessages] = useState([])

  async function fetchChat() {
    await fetch(
      `http://localhost:3000/chat?users=${props.user._id}&users=${props.data.id}`
    )
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setChat(data);
        setMessages(data.messages);
        props.joinRoom(data._id)
      });
  }

  async function postMessage() {
    await fetch(`http://localhost:3000/chat/${chat._id}/message?author=${props.user._id}`, {
      method: "POST",
      body: JSON.stringify({
        text: text,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });
  }

  const handleSubmit = (event) => {
    event.target.reset();
    event.preventDefault(); // 👈️ prevent page refresh
    if(text.trim().length !== 0 ) postMessage();
    setText("");
    // 👇️ clear all input values in the form
  };

  useEffect(() => {
   if(props.data) fetchChat();
  }, []);

  if(!props.data) return <div/>

  return (
    <div className="Chat">
      <div className="top">
        <div className="user">
          <img className="profile-img" src={props.data.img_url}></img>
          <div> {props.data.name} </div>
        </div>
        <FontAwesomeIcon
        icon={faXmark}
          onClick={() => {
            props.removeChat(props.data.id);
          }}
        />
      </div>
      <div className="content">
          {messages.map((message,i)=>{return <div key={i} className={message.author === props.user._id? "text user" : "text"}>{message.text}</div>})}
      </div>
      <div className="bottom">
        <form className="comment-form" onSubmit={handleSubmit} autoComplete="off">
          <input
            placeholder="Aa"
            name="text"
            onChange={(event) => setText(event.target.value)}
          ></input>
          <button type="submit"><FontAwesomeIcon icon={faReply}/></button>
        </form>
      </div>
    </div>
  );
}

export default Chat;
