import Post from "./Post";
import { useEffect, useState } from "react";
import { Navigate, useNavigate, useParams } from "react-router-dom";
import React from "react";
import Modal from "react-modal";
import CreatePost from "./CreatePost";
import EditPost from "./EditPost";
import "../styles/Timeline.css"

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Timeline(props) {
  const [posts, setPosts] = useState(null);
  const [postContent, setPostContent] = useState("");
  const [post_id, setPost_id] = useState(null);
  const [modalType,setModalType] = useState("edit")

  const navigate = useNavigate();
  const params = useParams();

  async function fetchPosts() {
    await fetch(`http://localhost:3000/user/${props.user._id}/posts`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setPosts(data);
      });
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
  }

  function closeModal() {
    setModalType("edit")
    setIsOpen(false);
  }



  if (props.user === null)
    return <Navigate to={process.env.PUBLIC_URL + "/login"} />;

  return (
    <div className="Timeline">
      <button onClick={()=>{setModalType("create");openModal()}}>New Post</button>
      {posts !== null &&
        posts.map((post, i) => {
          return (
            <Post
              user={props.user}
              post={post}
              key={i}
              openModal={openModal}
              setPostContent={setPostContent}
              setPost_id={setPost_id}
            />
          );
        })}
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
        ariaHideApp={false}
      >
        { modalType === "edit" && <EditPost postContent={postContent} post_id={post_id} posts={posts} setPosts={setPosts} closeModal={closeModal} />}
        { modalType === "create" &&
          <CreatePost user={props.user} setPosts={setPosts} closeModal={closeModal}/>
        }
      </Modal>
    </div>
  );
}

export default Timeline;
