import "./App.css";
import { useEffect, useState } from "react";
import Timeline from "./components/Timeline";
import {
  Routes,
  Route,
  Link,
  useNavigate,
  Navigate,
} from "react-router-dom";
import Login from "./components/Login";
import SignUp from "./components/SignUp";
import Profile from "./components/Profile";
import SearchResults from "./components/SearchResults";
import HeaderContent from "./components/HeaderContent";
import { parseWithOptions } from "date-fns/fp";




function App() {
  const [user, setUser] = useState(null);
  const [updateUser,setUpdateUser] = useState(false)
  const [searchResult,setSearchResult] = useState(null)
  const [isAuthenticated,setIsAuthenticated] = useState(false)




  useEffect(()=>{
    if(user) setIsAuthenticated(true)
  },[user])

  const PrivateRoute = ({isAuthenticated, children }) => {
    return isAuthenticated ? children : <Navigate to="/login" />;
  };

  async function signIn(username, password) {
    try {
      await fetch("http://localhost:3000/sign-in", {
        method: "POST",
        body: JSON.stringify({
          username: username,
          password: password,
        }),
        headers: { "content-type": "application/json" },
      })
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setUser(data);
          localStorage.setItem("user", JSON.stringify(data));
        });
    } catch (error) {}
  }

  async function updateUserData() {
    try {
      await fetch(`http://localhost:3000/user/${user._id}`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setUser(data);
          localStorage.setItem("user", JSON.stringify(data));
        });
    } catch (error) {}
  }

  useEffect(() => {
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      //Update data
      updateUserData()
      const foundUser = JSON.parse(loggedInUser);
      setUser(foundUser);
    }
  }, []);

  return (
      <div className="App">
        <div className="header">
          <HeaderContent isAuthenticated={isAuthenticated} setSearchResult={setSearchResult} user={user}/>
        </div>
        <Routes>

            <Route
              path={process.env.PUBLIC_URL + "/login"}
              element={<Login user={user} signIn={signIn} />}
            />

            <Route
              path={process.env.PUBLIC_URL + "/"}
              element={<PrivateRoute isAuthenticated={isAuthenticated}> <Timeline user={user} /> </PrivateRoute>}
            />

          <Route
            path={process.env.PUBLIC_URL + "/sign-up"}
            element={<SignUp user={user} signIn={signIn} />}
          />
          <Route
          user={user}
            path={process.env.PUBLIC_URL + "/user/:id/profile"}
            element={<PrivateRoute isAuthenticated={isAuthenticated} ><Profile user={user} /></PrivateRoute>}
          />
          <Route
            path={process.env.PUBLIC_URL + "/search"}
            element={<PrivateRoute isAuthenticated={isAuthenticated}><SearchResults users={searchResult} /></PrivateRoute>}
          />
        </Routes>
      </div>
  );
}

export default App;
